mapper.update:
UPDATE board
SET board_title = #{boardTitle},
	board_Content = #{boardContent}
WHERE board_no = #{boardNo}
AND board_pw = #{boardPw}

mapper.delete:
DELETE FROM board 
WHERE board_no = #{boardNo} 
AND board_pw = #{boardPw}

mapper.select:
SELECT board_no as boardNo, board_title as boardTitle, board_user as boardUser, board_date as boardDate FROM board
board_title like CONCAT('%', #{searchWord}, '%')
ORDER BY board_date DESC LIMIT #{beginRow}, #{rowPerPage}
